package com.eds.ma.mongodb.collection;import lombok.Data;import java.math.BigDecimal;import java.util.Date;/** * 设备软硬件交互信息Vo * @Author gaoyan * @Date: 2018/6/17 */@Datapublic class DeviceMessageRecord {    /**     * 数据流向类型 1:硬件上传 2:软件发送     * {@link com.eds.ma.socket.SocketConstants}     */    private Integer deviceFlowType;    /**     * 指令类型     * 指令类型： 1 周期上报 , 2 心跳包 , 0xA5 其它测量数据,5 锁定仪表,6 解锁仪表     */    private Long deviceType;    /**     * 数据长度     */    private Long deviceLength;    /**     * 仪表ID     */    private Long deviceId;    /**     * 设备状态     * {@link com.eds.ma.socket.DeviceCodeConstant}     */    private Long deviceCode;    /**     * 电池电量     */    private Long deviceElectricity;    /**     * 采样温度     */    private Long deviceTemperature;    /**     * 采样数值     */    private BigDecimal deviceReport;    /**     * 经度     */    private BigDecimal deviceLat;    /**     * 纬度     */    private BigDecimal deviceLng;    /**     * 创建时间     */    private Date created;    /**     * 是否有效     */    private Boolean dataStatus = true;}