package com.eds.ma.mongodb.collection;import com.eds.ma.mongodb.BaseDocument;import lombok.Data;import org.springframework.data.mongodb.core.mapping.Document;import java.math.BigDecimal;import java.util.Date;/** * 设备软硬件交互信息Vo * @Author gaoyan * @Date: 2018/6/17 */@Data@Document(collection="Device")public class MongoDevice extends BaseDocument {    /**     * 设备类型     */    private Long deviceKind;    /**     * 设备编号     */    private Long deviceCode;    /**     * 表示设备使用状态： 1表示检测工作中 0表示检测完毕待机中     */    private Integer deviceUseStatus;    /**     * 表示设备状态： 0 1 2 3     */    private Integer deviceStatus;    /**     * 表示NTC故障状态： 1表示NTC故障 0表示NTC正常     */    private Integer deviceNTCStatus;    /**     * 电池电量     */    private Integer deviceElectricity;    /**     * 表示恒温检测工作超时状态：1表示恒温超时设备故障 0表示恒温检测正常     */    private Integer deviceTemperature;    /**     * 经度     */    private BigDecimal deviceLat;    /**     * 纬度     */    private BigDecimal deviceLng;    /**     * 设备ICCID     */    private Long deviceICCID;}