package com.eds.ma.mongodb.collection;import com.eds.ma.mongodb.BaseDocument;import lombok.Data;import org.springframework.data.mongodb.core.mapping.Document;import org.springframework.data.mongodb.core.query.Update;import java.util.Date;/** * 设备软硬件交互信息Vo * @Author gaoyan * @Date: 2018/6/17 */@Data@Document(collection="DeviceHeartBeat")public class MongoDeviceHeartBeat extends BaseDocument {    /**     * 设备类型     */    private Long deviceKind;    /**     * 设备编号     */    private Long deviceCode;    /**     * 表示设备使用状态： 1表示检测工作中 0表示检测完毕待机中     */    private Long deviceUseStatus;    /**     * 甲醛检测数据更新状态 1表示检测完成数据更新； 0表示未检测完成     */    private Long deviceReportStatus;    /**     * 表示NTC故障状态： 1表示NTC故障 0表示NTC正常     */    private Long deviceNTCStatus;    /**     * 表示恒温检测工作超时状态：1表示恒温超时设备故障 0表示恒温检测正常     */    private Long deviceTemperatureStatus;    /**     * 表进气门电机故障状态：  1表示进气门电机故障设备故障  0表示进气门电机正常     */    private Long deviceIntakeValveStatus;    /**     * 电池低电压报警： 1表示电池低电量 0表示电池电量正常     */    private Long deviceElectricityStatus;    /**     * 设备归还状态     * 0 表示未归还,状态但未锁定     * 1 表示未归还,状态并锁定中     * 2 表示设备已归还状态但未锁定     * 3 表示设备已归还状态并锁定中     */    private Long deviceReturnStatus;    /**     * 设备信号强度     */    private Long deviceSignalStrength;    /**     * 创建时间     */    private Date created;    /**     * 时间戳     */    private Long timestamp;    /**     * 是否删除     */    private Integer dataStatus = 1;}