package com.eds.ma.mongodb.collection;import com.eds.ma.mongodb.BaseDocument;import com.eds.ma.socket.SocketConstants;import com.eds.ma.socket.message.MessageTypeConstants;import com.eds.ma.socket.util.SocketMessageUtils;import lombok.Data;import org.springframework.data.mongodb.core.mapping.Document;import java.util.Date;/** * 设备注册信息 * @Author gaoyan * @Date: 2018/6/17 */@Data@Document(collection="DeviceRegister")public class MongoDeviceRegister extends BaseDocument {    /**     * 设备类型     */    private Long deviceKind;    /**     * 设备编号     */    private Long deviceCode;    /**     * 报文编号     */    private Long messageNo;    /**     * 设备ICCID     */    private String deviceICCID;    /**     * 创建时间     */    private Date created;    /**     * 时间戳     */    private Long timestamp;    /**     * 是否删除     */    private Integer dataStatus = 1;    public Long sum(){        return deviceKind+deviceCode+messageNo;    }}