mapper.select:
SELECT ed.id AS deviceId,ed.deviceOriginCode,ed.deviceGpsNo, es.spLng,es.spLat,ed.deviceStatus,ed.spId, ed.orderId,ed.userId,eo.rentTime,eo.orderStatus, ed.adjustParamA1,ed.adjustParamA2,ed.adjustParamA3,ed.adjustParamB1,ed.adjustParamB2,ed.adjustParamB3 FROM t_eds_device ed LEFT JOIN t_eds_sp es ON ed.spId = es.id left join t_eds_order eo on eo.id = ed.orderId WHERE ed.id = #{deviceId} AND es.dataStatus = 1 AND ed.dataStatus = 1
and ed.userId = #{userId}
FOR UPDATE

mapper.update:
UPDATE t_eds_device
SET borrowTimes = borrowTimes + 1,userId = #{userId},orderId = #{orderId},deviceStatus = 'S_SPZT_SYZ'
WHERE id=#{deviceId}

mapper.select:
SELECT df.title,df.detail
FROM t_eds_device_faq df
WHERE  df.dataStatus = 1
order by priority desc

    LIMIT #{start}, #{pageSize}


    LIMIT #{pageSize}
