mapper.select:
SELECT o.id,o.orderCode,o.orderStatus,sc.sysName AS orderStatusDesc,o.rentTime, o.rentSpId,o.deviceId,o.userId,o.totalFee,s.spName,s.spLat,s.spLng FROM t_eds_order o LEFT JOIN t_eds_sp s ON o.rentSpId = s.id LEFT JOIN t_b_sys_code sc ON o.orderStatus = sc.sysCode WHERE o.userId = #{userId} AND o.dataStatus = 1
and o.orderStatus = #{orderStatus}
ORDER BY o.orderStatus desc,o.created DESC LIMIT #{start}, #{pageSize}
